package modulo1;

public class Ejercicio8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Este es el ejercicio n°8");
		
		//OPERACIONES SUMA Y RESTA
		
		byte numero1 = 5;
		byte numero2 = 10;
		byte suma;
		suma= (byte)(numero1 + numero2);
		System.out.println(suma);
		
		byte resta;
		resta= (byte)(numero2 - numero1);
		System.out.println(resta);
		
		//OPERACIONES PRODUCTO Y DIVISION
		
		double valor = 20.5;
		double valor2= 5.10;
		double producto;
		producto= valor * valor2;
		System.out.println(producto);
		
		double division;
		division= valor / valor2;
		System.out.println(division);
		
	}

}
